(function(Backbone) {
    /**
     * Before callback para modelos en backbone.
     * Agregar el método before al modelo
     */
    Backbone.Model.prototype.save = function() {
        var save = Backbone.Model.prototype.save;
        return function() {
            if (typeof this.before === 'function') {
                var before = this.before.call(this, arguments);
                // si before devuelve true cancelamos la operación save
                if (before === true) {
                    return;
                }
            }

            return save.apply(this, arguments);
        };
    }();
})(Backbone);
